function bubbleSort() {
    for (let i = 0; i < nums.length - 1; i++) {
        for (let j = 0; j < nums.length - i - 1; j++) {
            if (nums[j] > nums[j+1]) {
                swapNums(j,j+1);
                delayDrawGraph(nums, recentA, recentB);
            }
        }
    }
}

function selectionSort() {
    for (let i = 0; i < nums.length - 1; i++) {
        let min_idx = i;
        for (j = i + 1; j < nums.length; j++) {
            if (nums[j] < nums[min_idx]) {
                min_idx = j;
            }
        }

        if (min_idx != i) {
            swapNums(min_idx, i);
            delayDrawGraph(nums, recentA, recentB);
        }

    }
}

function insertionSort() {
    for (let i = 1; i < nums.length; i++) {
        let j = i;

        while (j > 0 && nums[j] < nums[j-1]) {
            swapNums(j, j - 1);
            delayDrawGraph(nums, recentA, recentB);
            j--;
        }    
    }
}

function merge(l, m, r) {
    let l2 = m + 1;

    if (nums[m] <= nums[l2]) return;

    while (l <= m && l2 <= r) {
        if (nums[l] <= nums[l2]) {
            l++;
        }
        else {
            let val = nums[l2];
            let ind = l2;

            while (ind != l) {
                nums[ind] = nums[ind - 1];
                delayDrawGraph(nums, ind, -1);
                ind--;
            }
            nums[l] = val;
            delayDrawGraph(nums, l, -1);

            l++; m++; l2++;
        }
    }
}

function mergeSort(l = 0, r = nums.length - 1) {
    if (l < r) {
        let m = Math.floor((l + r) / 2);

        mergeSort(l, m);
        mergeSort(m + 1, r);
        merge(l, m, r);
    }
}

function heapify(N, i) {
    let largest = i;

    let l = 2 * i + 1;
    let r = 2 * i + 2;

    if (l < N && nums[l] > nums[largest]) largest = l;
    if (r < N && nums[r] > nums[largest]) largest = r;

    if (largest != i) {
        swapNums(i, largest);
        delayDrawGraph(nums, recentA, recentB);
        
        heapify(N, largest);
    }
}
function heapSort() {
    for (let i = Math.floor(nums.length / 2) - 1; i >= 0; i--) {
        heapify(nums.length, i);
    }
    for (let i = nums.length - 1; i > 0; i--) {
        swapNums(0, i);
        delayDrawGraph(nums, recentA, recentB);
        
        heapify(i, 0);
    }
}

function partition(low, high) {
    let pivot = nums[high];

    let i = low - 1;

    for (let j = low; j <= high - 1; j++) {
        if (nums[j] < pivot) {
            i++;
            swapNums(i, j);
            delayDrawGraph(nums, recentA, recentB);
        }
    }

    swapNums(i + 1, high);
    delayDrawGraph(nums, recentA, recentB);
    return i + 1;
}
function quickSort(low = 0, high = nums.length - 1) {
    if (low < high) {
        
        let pi = partition(low, high);

        quickSort(low, pi - 1);
        quickSort(pi + 1, high);
    }
}

function shellSort() {
    for (let gap = Math.floor(nums.length / 2); gap > 0; gap = Math.floor(gap/2)) {
        for (let i = gap; i < nums.length; i++) {
            let temp = nums[i];
            let j;
            for (j = i; j >= gap && nums[j - gap] > temp; j -= gap) {
                nums[j] = nums[j - gap];
                delayDrawGraph(nums, j, -1);
            }

            nums[j] = temp;
            delayDrawGraph(nums, j, -1);
        }
    }
}

function gnomeSort() {
    let i = 0;

    while (i < nums.length) {
        if (i == 0) i++;
        if (nums[i] > nums[i-1]) i++;
        else {
            swapNums(i, i-1);
            delayDrawGraph(nums, recentA, recentB);
            i--;
        }
    }
}

function bogoSort() {
    let timeout = 0;
    while (!isSorted(nums)) {
        timeout++;
        for (let i = nums.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            swapNums(i, j);
            
        }
        delayDrawGraph(nums, recentA, recentB);
        if (timeout > 10000) break;
    }
}

function stoogeSort(i = 0, j = nums.length - 1) {
    if (nums[i] > nums[j]) {
        swapNums(i, j);
        delayDrawGraph(nums, recentA, recentB);
    }
    if ((j - i + 1) > 2) {
        let t = Math.floor((j - i + 1) / 3);
        stoogeSort(i, j-t);
        stoogeSort(i+t, j);
        stoogeSort(i, j-t);
    }
}